import React, {useState} from "react";
import './quiz.css';

function Quiz({data: {question, answers, correct_answers}}) {
    const [score, setscore] = useState(0);
    function check(value){
        if(value==={correct_answers})
        {
           setscore(score+1);
        }

    }

  return (
      <div className="main">
    <div className="container">
    <span>Score : {score}</span>
        <div className="topbox">
      <h2>{question}</h2>
      </div>
      <div className="answerbox">
          <button onClick={()=>{check("answer_a")}}>{answers.answer_a}</button>
          <button onClick={()=>{check("answer_b")}}>{answers.answer_b}</button>
          <button onClick={()=>{check("answer_c")}}>{answers.answer_c}</button>
          <button onClick={()=>{check("answer_d")}}>{answers.answer_d}</button>
      </div>
    </div>
    </div>
  );
}

export default Quiz;
