import React from "react";
import './quiz.css';

function Quiz({data: {question, answers, correct_answers}}) {
    function check(value){
        if({value}==={correct_answers})
        {
           this.style.background.color="green";
        }

    }

  return (
      <div className="main">
    <div className="container">
    <span>Score : 0</span>
        <div className="topbox">
      <h2>{question}</h2>
      </div>
      <div className="answerbox">
          <button value="answer_a" onClick={check(this.value)}>{answers.answer_a}</button>
          <button value="answer_b">{answers.answer_b}</button>
          <button value="answer_c">{answers.answer_c}</button>
          <button value="answer_d">{answers.answer_d}</button>
      </div>
    </div>
    </div>
  );
}

export default Quiz;
