import React, { useState } from "react";
import "./quiz.css";

function Quiz({ data: { question, answers, correct_answers } }) {
  const [score, setscore] = useState(0);
  function check(correct) {
    if (correct === "true") {
        setscore(score + 1);
        this.button.className="final";
    }
  }

  return (
    <div className="main">
      <div className="container">
        <span>Score : {score}</span>
        <div className="topbox">
          <h2>{question}</h2>
        </div>
        <div className="answerbox">
          <button className="initial"
            onClick={() => {
              check(correct_answers.answer_a_correct);
            }}
          >
            {answers.answer_a}
          </button>
          <button
            onClick={() => {
              check(correct_answers.answer_b_correct);
            }}
          >
            {answers.answer_b}
          </button>
          <button
            onClick={() => {
              check(correct_answers.answer_c_correct);
            }}
          >
            {answers.answer_c}
          </button>
          <button
            onClick={() => {
              check(correct_answers.answer_d_correct);
            }}
          >
            {answers.answer_d}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Quiz;
