import React, { useState } from "react";
import "./quiz.css";

function Quiz({ data: { question, answers, correct_answers } }, {indexx}) {
  const [score, setscore] = useState(0);
  const [res, setres] = useState("");
  const [wrong, setwrong] = useState(0);
  function check(correct) {
    if (correct === "true") {
      setscore(score + 1);
      setres("Yayy! Correct answer ðŸŽ‰");
      document.querySelector("button").className="final";
    } else {
        setwrong(wrong+1);
        setres("Oops, Wrong answer");
    }
  }
  const final=score+wrong;



  return (
    <div className="main">
      <div className="container">
        <div className="head">
          <span>Score : {score}</span><br/>
          <span>Your score is {score}/{final}</span>
        </div>
        <div className="topbox">
          <h2>{question}</h2>
        </div>
        <div className="answerbox">
          <button className="initial"
            onClick={() => {
              check(correct_answers.answer_a_correct);
            }}
          >
            {answers.answer_a}
          </button>
          <button className="initial"
            onClick={() => {
              check(correct_answers.answer_b_correct);
            }}
          >
            {answers.answer_b}
          </button>
          <button className="initial"
            onClick={() => {
              check(correct_answers.answer_c_correct);
            }}
          >
            {answers.answer_c}
          </button>
          <button className="initial"
            onClick={() => {
              check(correct_answers.answer_d_correct);
            }}
          >
            {answers.answer_d}
          </button>
          <p>{res}</p>
        </div>
      </div>
    </div>
  );
}

export default Quiz;
