import React, { useState } from 'react';
import './App.css';
import Categories from './components/categories';
import Quiz from './components/quiz';
import data from './components/reload';


function App() {
  const [i, seti] = useState(0);
  function appear(){
    var a=true;
    if(a){
      Quiz.style.display="block";
      a=false;
    }
    else
    Quiz.style.display="none";
  }
  return (
    <div className="App">
      <header className="App-header">
        <h1>QuizKy</h1>
        <h3><i>{data[i]}</i></h3>
        <i class="fas fa-sync-alt" onClick={()=>seti(i+1)}></i>
        </header>
        <div className="Cat">
        <Categories name="Coding" onClick={this.appear}></Categories>
        <Categories name="Entertainment"></Categories>
        <Categories name="Science"></Categories>
        <Categories name="Sports"></Categories>
        </div>
        <Quiz></Quiz>

        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
    </div>
  );
}

export default App;
