import React, { useEffect, useState } from "react";
import "./App.css";
import Categories from "./components/categories";
import Headerr from "./components/header";
import Quiz from "./components/quiz";

const apii = "https://opentdb.com/api.php?amount=10";
function App() {
  const [question, setQuestion] = useState([]);
  useEffect(() => {
    fetch(apii)
      .then((res) => res.json())
      .then((data) => {
        setQuestion(data.results);
        console.log(data);
      });
  }, []);

  return (question.length>0 ?(
    <div className="App">
      <Headerr></Headerr>
      <div className="Cat">
        <Categories name="Coding"></Categories>
        <Categories name="Entertainment"></Categories>
        <Categories name="Science"></Categories>
        <Categories name="Sports"></Categories>
      </div>
      <Quiz data={question[0]}></Quiz>
      <p>
        Edit <code>src/App.js</code> and save to reload.
      </p>
      <a
        className="App-link"
        href="https://reactjs.org"
        target="_blank"
        rel="noopener noreferrer"
      >
        Learn React
      </a>
    </div>
  ): "Loading...");
}

export default App;
