import React, { useState } from "react";
import "./quiz.css";

function Quiz({ data: { question, answers, correct_answers } }, index) {
  const [score, setscore] = useState(0);
  const [res, setres] = useState("");
  function check(correct) {
    if (correct === "true") {
      setscore(score + 1);
      setres("Yayy! Correct answer ðŸŽ‰");
    } else setres("Oops, Wrong answer");
  }



  return (
    <div className="main">
      <div className="container">
        <div className="head">
          <span>Score : {score}</span>
          <span>Your score is {score}/{index}</span>
        </div>
        <div className="topbox">
          <h2>{question}</h2>
        </div>
        <div className="answerbox">
          <button
            onClick={() => {
              check(correct_answers.answer_a_correct);
            }}
          >
            {answers.answer_a}
          </button>
          <button
            onClick={() => {
              check(correct_answers.answer_b_correct);
            }}
          >
            {answers.answer_b}
          </button>
          <button
            onClick={() => {
              check(correct_answers.answer_c_correct);
            }}
          >
            {answers.answer_c}
          </button>
          <button
            onClick={() => {
              check(correct_answers.answer_d_correct);
            }}
          >
            {answers.answer_d}
          </button>
          <p>{res}</p>
        </div>
      </div>
    </div>
  );
}

export default Quiz;
